const display=document.querySelector(".calculator-display"),buttons=Array.from(document.getElementsByClassName("calculator-keyboard-button"));buttons.map(e=>{e.addEventListener("click",function(event){switch(event.target.innerText){case"AC":display.innerText="";break;case"‚Üê":display.innerText=display.innerText.slice(0,-1);break;case"=":display.innerText&&(display.innerText=eval(display.innerText));break;default:display.innerText+=event.target.innerText}})}),console.log(display);let hr=document.querySelector("#hr"),min=document.querySelector("#min"),sec=document.querySelector("#sec");setInterval(function(){let e=new Date;var t=30*e.getHours(),n=6*e.getMinutes(),r=6*e.getSeconds();hr.style.transform=`rotateZ(${t+n/12}deg)`,min.style.transform=`rotateZ(${n}deg)`,sec.style.transform=`rotateZ(${r}deg)`});